(1858, 1, '2019-05-23 21:13:59', '2019-05-23 21:13:59', 'https://twitter.com/nacin/status/319508408669708289\r\n\r\nThis post tests WordPress\' <a title=\"Twitter Embeds\" href=\"https://en.support.wordpress.com/twitter/twitter-embeds/\" target=\"_blank\" rel=\"noopener noreferrer\">Twitter Embeds</a> feature.', 'Media: Twitter Embeds', '', 'inherit', 'closed', 'closed', '', '1179-revision-v1', '', '', '2019-05-23 21:13:59', '2019-05-23 21:13:59', '', 1179, 'http://ztheme.loc/1179-revision-v1/', 0, 'revision', '', 0),
(1859, 1, '2019-05-23 21:15:37', '2019-05-23 21:15:37', 'This post is scheduled to be published in the future.\r\n\r\nIt should not be displayed by the theme.\r\n\r\n<img class=\"alignnone size-medium wp-image-1045\" src=\"http://ztheme.loc/wp-content/uploads/2013/03/unicorn-wallpaper-300x225.jpg\" alt=\"Unicorn Wallpaper\" width=\"300\" height=\"225\" />', 'Scheduled', '', 'inherit', 'closed', 'closed', '', '1153-revision-v1', '', '', '2019-05-23 21:15:37', '2019-05-23 21:15:37', '', 1153, 'http://ztheme.loc/1153-revision-v1/', 0, 'revision', '', 0),
